const rawOutputString = $input.first().json.output;

// 2. Validation: Check if input exists
if (!rawOutputString || typeof rawOutputString !== 'string') {
  return [{ 
    json: { 
      error: "Input field 'output' is missing or not a string. Check AI Agent settings.",
      originalInput: $input.first().json 
    } 
  }];
}

let jsonString = null;

// --- STRATEGY 1: Regex for Markdown Code Blocks (```json ... ```) ---
// Captures content between triple backticks, optionally ignoring the word 'json'
const markdownRegex = /`{3,}(?:json)?\s*([\s\S]*?)\s*`{3,}/i;
const markdownMatch = rawOutputString.match(markdownRegex);

if (markdownMatch && markdownMatch[1]) {
  jsonString = markdownMatch[1].trim();
}

// --- STRATEGY 2: Brute Force (Find first '{' and last '}') ---
// Useful if the AI chats before/after the JSON without code blocks.
if (!jsonString) {
  const startIndex = rawOutputString.indexOf('{');
  const endIndex = rawOutputString.lastIndexOf('}');

  if (startIndex !== -1 && endIndex !== -1 && endIndex > startIndex) {
    jsonString = rawOutputString.substring(startIndex, endIndex + 1).trim();
  }
}

// 3. If no JSON-like structure found, return error
if (!jsonString) {
  return [{ 
    json: { 
      error: "No JSON object found in the AI response.",
      originalOutput: rawOutputString
    }
  }];
}

// 4. Parse the JSON
try {
  // Attempt clean parse
  const parsedJson = JSON.parse(jsonString);
  return [{ json: parsedJson }];
} catch (error) {
  // 5. Retry Strategy: Fix common AI JSON errors
  try {
    // Fix 1: Escape unescaped newlines inside strings (common in addresses/notes)
    // This regex looks for newlines that are NOT preceded by a valid escape char
    // Note: This is a simple heuristic; strictly broken JSON is hard to fix perfectly.
    const fixedJsonString = jsonString.replace(/(?<!\\)\n/g, "\\n");

    const parsedJsonRetry = JSON.parse(fixedJsonString);
    return [{ json: parsedJsonRetry }];
  } catch (retryError) {
    // Final Failure
    return [{ 
      json: {
        error: "JSON Parsing Failed",
        details: retryError.message,
        extractedString: jsonString,
        originalOutput: rawOutputString
      }
    }];
  }
}

const results = [];

// Helper function to clean floating point errors
// Example: "31.177160999999995" -> "31.177161"
function cleanCoordinate(val) {
  if (!val) return null;
  // 1. Parse as float
  // 2. Fix to 7 decimal places (high precision) to round off the .99999...
  // 3. Parse again to remove trailing zeros (e.g. "34.50000" becomes 34.5)
  // 4. Return as string
  return parseFloat(parseFloat(val).toFixed(7)).toString();
}

for (const item of items) {
  const html = item.json.data || "";
  const locationHeader = item.json.headers && item.json.headers.location ? item.json.headers.location : "";

  let lat = null;
  let long = null;
  let method = null;

  // ---------------------------------------------------------
