  // ---------------------------------------------------------
  // Matches: q=31.177,34.781 (and handles HTML entities like &amp; before it)
  // ---------------------------------------------------------
  const qParamRegex = /q=(-?\d+\.\d+)(?:%2C|\+|%20|%2B|,\s*|,\+|&|,|;| )+(-?\d+\.\d+)/i;
  const qMatch = html.match(qParamRegex);

  if (qMatch) {
    lat = qMatch[1];
    long = qMatch[2];
    method = "priority_1_url_q_param";
  }

  // ---------------------------------------------------------
  // PRIORITY 2: Javascript State (For Hotels/Places)
  // ---------------------------------------------------------
  // Used if 'q' is text.
  // Note: In this JS array, Longitude (index 1) is BEFORE Latitude (index 2)
  // ---------------------------------------------------------
  if (!lat) {
    const stateRegex = /window\.APP_INITIALIZATION_STATE=\s*\[\[\[[^,]+,(-?\d+\.\d+),(-?\d+\.\d+)\]/;
    const stateMatch = html.match(stateRegex);

    if (stateMatch) {
      long = stateMatch[1];
      lat = stateMatch[2];
      method = "priority_2_app_state";
    }
  }

  // ---------------------------------------------------------
  // PRIORITY 3: Static Map (Strict 'Markers' Only)
  // ---------------------------------------------------------
  if (!lat) {
    const staticMapRegex = /staticmap\?.*?(?:markers)=(-?\d+\.\d+)(?:%2C|,|\+|%20)+(-?\d+\.\d+)/i;
    const metaMatch = html.match(staticMapRegex);

    if (metaMatch) {
      lat = metaMatch[1];
      long = metaMatch[2];
      method = "priority_3_static_map_marker";
    }
  }

  // ---------------------------------------------------------
  // PRIORITY 4: Redirect Headers
  // ---------------------------------------------------------
  if (!lat && locationHeader) {
    let targetUrl = locationHeader;
    if (targetUrl.includes("continue=")) {
      try {
        const params = new URLSearchParams(targetUrl.split('?')[1]);
        targetUrl = params.get('continue') || targetUrl;
      } catch (e) {}
    }

    const headerRegex = /@(-?\d+\.\d+),(-?\d+\.\d+)/;
    const headerMatch = targetUrl.match(headerRegex);

    if (headerMatch) {
      lat = headerMatch[1];
      long = headerMatch[2];
      method = "priority_4_header";
    }
  }

  // ---------------------------------------------------------
  // FINAL STEP: Clean and Format Numbers
  // ---------------------------------------------------------
  results.push({
    json: {
      latitude: cleanCoordinate(lat),
      longitude: cleanCoordinate(long)
    }
  });
}

return results;

