    data.addresses.forEach((addr, index) => {
      // Map Index (1, 2) to DB ID
      contextText += `  ${index + 1}. ${addr.address_text} (ID: ${addr.id})\n`;
    });
    contextText += `INSTRUCTION: If user selects an address, use the ID provided.\n`;
  } else {
    contextText += `\nSAVED ADDRESSES: None. (Ask for Location Pin or Link).\n`;
  }
}

// 4. Output Separated Fields
return [{
  json: {
    // 1. The pure message the user sent
    user_message: userMessage,

    // 2. The background info the AI needs to know
    system_context: contextText,

    // 3. IDs passed through for the Router/Execution nodes later
    customer_id: (data && data.found) ? data.customer.id : null,
    active_order_id: (data && data.active_order) ? data.active_order.id : null
  }
}];

/**
 * n8n Code Node: Typing Time Calculator
 *
 * This node simulates the time it would take for a human to type a message.
 * It takes an incoming message, counts the characters, and for each character,
 * it calculates a random delay within a specified range.
 *
 * HOW TO USE:
 * 1. Copy and paste this entire code into an n8n "Code" node.
 * 2. Make sure the input data for this node has a property containing the message you want to analyze.
 * By default, this code looks for `items[0].json.message`. You may need to change
 * `item.json.message` to match your actual incoming data structure (e.g., `item.json.text`).
 * 3. Adjust the `minTimePerChar` and `maxTimePerChar` variables below to set your desired time range.
 */

// --- Configuration ---
// Set the minimum time (in milliseconds) it takes to type one character.
const minTimePerChar = 20;

// Set the maximum time (in milliseconds) it takes to type one character.
const maxTimePerChar = 50;
// -------------------

// The n8n Code node executes this code for all incoming items.
// We will loop through each item, perform the calculation, and return it with the new data.
for (const item of items) {
  // Get the message from the incoming item's JSON data.
  // IMPORTANT: Change 'message' if your field is named something else (e.g., 'text', 'body').
  // The '|| ""' part ensures that if the message field doesn't exist, we use an empty string to avoid errors.
  const message = $input.first().json.reply_to_user || '';

  // Get the total number of characters in the message.
  const characterCount = message.length;

  // Initialize total time to 0.
  let totalTypingTime = 0;

  // Loop through each character of the message to calculate the time.
  for (let i = 0; i < characterCount; i++) {
    // Generate a random typing time for this specific character.
    // The time will be between minTimePerChar and maxTimePerChar.
    const randomTimeForChar = Math.random() * (maxTimePerChar - minTimePerChar) + minTimePerChar;

    // Add this character's time to the total.
    totalTypingTime += randomTimeForChar;
  }

  // Add the calculated results to the item's JSON data.
  // The original data from the item will be preserved.
  item.json.typingAnalysis = {
    characterCount: characterCount,
    // We use Math.round() to get a clean integer value for the total time.
    calculatedTimeMs: Math.round(totalTypingTime),
    originalMessage: message,
  };
}

// Return all the processed items to be used in the next nodes.
return items;

// Loop through all incoming items
for (const item of items) {
  // Safe access to data structure
  const body = item.json.body || {};
  const data = body.data || {};
  const key = data.key || {};
  const message = data.message || {};

  // 1. Logic to get the JID with 's.whatsapp.net'
  let correctJid = '';

  if (key.remoteJid && key.remoteJid.includes('s.whatsapp.net')) {
    correctJid = key.remoteJid;
  } else if (key.remoteJidAlt && key.remoteJidAlt.includes('s.whatsapp.net')) {
    correctJid = key.remoteJidAlt;
  } else {
    // Fallback if neither has it, default to remoteJid
    correctJid = key.remoteJid || '';
  }

  // 2. Extract the phone number (everything before the @)
  const phoneNumber = correctJid.split('@')[0];

  // 3. Handle Timestamp (Standard JS, convert Seconds to Date + 3 Hours for UTC+3)
  let formattedTime = '';
  if (data.messageTimestamp) {
    // Timestamp is in seconds, multiply by 1000 for milliseconds
    const date = new Date(data.messageTimestamp * 1000);
    // Add 3 hours in milliseconds (3 * 60 * 60 * 1000 = 10800000)
    const utc3Date = new Date(date.getTime() + 10800000);
    // Convert to ISO string for consistency
    formattedTime = utc3Date.toISOString().replace('T', ' ').replace('Z', '');
  }

  // 4. Construct the Output JSON
  item.json = {
    SenderJid: correctJid,
    SenderName: data.pushName,
    Timestamp: formattedTime,
    MsgType: data.messageType,
    Msg: message.conversation,
    Media: message.base64, // Will be undefined if not present, which is fine
    Instance: body.instance,
    Key: body.apikey,
    phonenumber: phoneNumber
  };
}

return items;
